import React, { useState, useEffect } from 'react';
import './App.css';
import axios from './axios';
import AddTask from './components/Task/AddTask';
import Task from './components/Task/Task';
function App() {
  const [tasks, setTasks] = useState([]);

  const [editTask, setEditTask] = useState(false);

  const initialEditState = {
    id: null,
    title: '',
    timeStart: '',
    timeEnd: '',
  };
  const [curentTask, setCurentTask] = useState(initialEditState);

  const editCurrentTask = (task) => {


    setEditTask(true);
    setCurentTask({
      id: task.id,
      title: task.title,
      timeStart: task.timeStart,
      timeEnd: task.timeEnd,
    });
  };

  const getTasks = async () => {
    const res = await axios.get('/tasks').catch((err) => console.log(err));
    if (res && res.data) {
      setTasks(res.data);
    }
  };
  // console.log(tasks.length);

  const addTask = async (task) => {
    console.log(task);
    const req = {
      id: tasks.length + 1,
      ...task,
    };
    console.log(req);
    const res = await axios.post('/tasks', req);
    setTasks([...tasks, res.data]);
  };

  const deleteTask = async (id) => {
    await axios.delete(`/tasks/${id}`);
    const newTasksList = tasks.filter((task) => {
      return task.id !== id;
    });
    setTasks(newTasksList);
  };
  useEffect(() => {
    getTasks();
  }, []);
  // useEffect(() => {}, [tasks]);
  return (
    <div className='container'>
      <h1>CRUD App with Hooks</h1>
      <div className='flex-row'>
        <div className='flex-large'>
          <h2>Add user</h2>
          <AddTask addTask={addTask} />
        </div>
        <div className='flex-large'>
          <h2>View users</h2>
          <Task
            tasks={tasks}
            deleteTask={deleteTask}
            editCurrentTask={editCurrentTask}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
