import React from 'react';
import './Task.css';

const Task = (props) => {
    const renderTasks = props.tasks.map(task => {
        return(
            <div className='task-wrapper'>
      <div className='task-container'>
        {/* <div className='task-id'>{id}</div> */}
        <div className='task-name'>{task.title}</div>
        <div className='time-start'>{task.timeStart}</div>
        <div className='time-end'>{task.timeEnd}</div>
      </div>

      <span className='task-remove'>
        <i className='fas fa-times' onClick={() => props.onDelete(id)}></i>
      </span>

      <span className='task-edit'>
        <Link to={{ pathname: `/tasks${id}` }}>
          <i className='far fa-edit' style={{ color: 'green' }}></i>
        </Link>
      </span>
    </div>
        )
    })
  return <div>{renderTasks}</div>;
};

export default Task;
