import {
  Formik,
  Form,
  Field,
  FormikHelpers,
  ErrorMessage,
  FormikErrors,
  FormikTouched,
} from "formik"
import * as Yup from "yup"
import "./login.css"

interface FormValues {
  email: string
  password: string
}
const initialValues: FormValues = { email: "", password: "" }

// const LoginValidation = Yup.object().shape({
//   email: Yup.string().email().required(),
//   password: Yup.string()
//     .required("Please enter your password")
//     .min(8, "Password is too short - should be 8 chars minimum.")
//     .matches(/[A-Z]+/, "One uppercase character")
//     .matches(/[a-z]+/, "One lowercase character")
//     .matches(/[a@$!%#?&]+/, "One special character")
//     .matches(/\d+/, "One number"),
// })
const validate = (values: FormValues) => {
  const errors: FormikErrors<FormValues> = {}
  if (!values.email) {
    errors.email = "Required"
  } else if (values.email.length > 15) {
    errors.email = "Must be 15 characters or less"
  }

  if (!values.password) {
    errors.lastName = "Required"
  } else if (values.lastName.length > 20) {
    errors.lastName = "Must be 20 characters or less"
  }

  if (!values.email) {
    errors.email = "Required"
  } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
    errors.email = "Invalid email address"
  }

  return errors
}
const renderError = (message: string) => (
  <p className='error-mesage'>{message}</p>
)
const Login = () => (
  <div className='login-container'>
    <Formik
      initialValues={initialValues}
      validationSchema={LoginValidation}
      onSubmit={(
        values: FormValues,
        { setSubmitting }: FormikHelpers<FormValues>
      ) => {
        setTimeout(() => {
          alert(JSON.stringify(values, null, 2))
          setSubmitting(false)
        }, 500)
      }}
    >
      {({ errors, touched, isValid }) => {
        console.log(errors.password)

        return (
          <Form className='login-form'>
            <div className='inputField'>
              <div className='formField'>
                <label> Email</label>

                <Field
                  id='email'
                  name='email'
                  // type='text'
                  placeholder='Enter your email'
                  autoComplete='off'
                  // value={values.email}
                  errors={errors}
                  touched={touched}
                />
              </div>
              {/* <ErrorMessage name='email' render={renderError} /> */}
            </div>
            <div className='inputField'>
              <div className='formField'>
                <label> Password</label>
                <Field
                  id='password'
                  name='password'
                  type='password'
                  placeholder='Enter your password'
                  autoComplete='off'
                  // value={values.password}
                  errors={errors}
                  touched={touched}
                />
              </div>
            </div>
            {/* <ErrorMessage name='password' render={renderError} /> */}

            {errors && <p style={{ color: "red" }}> {errors.password} </p>}

            <button disabled={!isValid}>Signin</button>
          </Form>
        )
      }}
    </Formik>
  </div>
)

export default Login
