import React, { useState } from 'react';
import '../Task/AddTasks.css';
const AddTask = (props) => {
  const initialFormState = {
    id: null,
    title: '',
    timeStart: '',
    timeEnd: '',
  };
  const [task, setTask] = useState(initialFormState);
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTask({ ...task, [name]: value });
  };
  const handlerSubmit = (e) => {
    e.preventDefault();
    if (!task.title || !task.timeEnd || !task.timeStart) return;
    props.AddTask(task);
    setTask(initialFormState);
  };
  return (
    <div className='add-form'>
      <h2>Add Tasks for today</h2>
      <form onSubmit={handlerSubmit}>
        <div className='new-task_controls'>
          {/* <div className='new-task_control'>
            <label>Task # </label>
            <input
              type='number'
              name='task-id'
              placeholder='Add your task'
              value={enteredId}
              onChange={handlerId}
            />
          </div> */}
          <div className='new-task_control'>
            <label>Task </label>
            <input
              type='text'
              name='task'
              placeholder='Add your task'
              value={task.title}
              onChange={handleInputChange}
            />
          </div>
          <div className='new-task_control'>
            <label>Time Start</label>
            <input
              type='text'
              name='timeStart'
              placeholder='When do you want to start'
              value={task.timeStart}
              onChange={handleInputChange}
            />
          </div>
          <div className='new-task_control'>
            <label>Time End</label>
            <input
              type='text'
              name='timeEnd'
              placeholder='When do you want to finish'
              value={task.timeEnd}
              onChange={handleInputChange}
            />
          </div>
        </div>
        <div className='new-task_actions'>
          <button type='submit'>Add Task</button>
        </div>
      </form>
    </div>
  );
};

export default AddTask;
