import React from 'react';
import './Task.css';
import { Link } from 'react-router-dom';
const Task = (props) => {
  return (
    <div className='task-wrapper'>
      <div className='task-container'>
        {/* <div className='task-id'>{id}</div> */}
        <div className='task-name'>{title}</div>
        <div className='time-start'>{timeStart}</div>
        <div className='time-end'>{timeEnd}</div>
      </div>

      <span className='task-remove'>
        <i className='fas fa-times' onClick={() => props.onDelete(id)}></i>
      </span>

      <span className='task-edit'>
        <i className='far fa-edit' style={{ color: 'green' }}></i>
      </span>
    </div>
  );
};

export default Task;
