import React, { useState, useEffect } from 'react';
import './App.css';
import axios from './axios';
import AddTask from './components/Task/AddTask';
import EditTask from './components/Task/EditTasks';
import Task from './components/Task/Task';
function App() {
  const [tasks, setTasks] = useState([]);

  const [editing, setEditing] = useState(false);

  const initialFormState = {
    id: null,
    title: '',
    timeStart: '',
    timeEnd: '',
  };
  const [curentTask, setCurentTask] = useState(initialFormState);

  const editTask = (task) => {
    setEditing(true);
    setCurentTask({
      id: task.id,
      title: task.title,
      timeStart: task.timeStart,
      timeEnd: task.timeEnd,
    });
  };

  const updateTask = (id, updatedTask) => {
    setEditing(false);
    setTasks(tasks.map((task) => (task.id === id ? updatedTask : task)));
  };
  const getTasks = async () => {
    const res = await axios.get('/tasks').catch((err) => console.log(err));
    if (res && res.data) {
      setTasks(res.data);
    }
  };
  // console.log(tasks.length);

  const addTask = async (task) => {
    console.log(task);
    const req = {
      id: tasks.length + 1,
      ...task,
    };
    console.log(req);
    const res = await axios.post('/tasks', req);
    setTasks([...tasks, res.data]);
  };

  const deleteTask = async (id) => {
    await axios.delete(`/tasks/${id}`);
    const newTasksList = tasks.filter((task) => {
      return task.id !== id;
    });
    setTasks(newTasksList);
  };
  useEffect(() => {
    getTasks();
  }, []);
  // useEffect(() => {}, [tasks]);
  return (
    <div className='container'>
      <h1>CRUD App with Hooks</h1>
      <div className='flex-row'>
        <div className='flex-large'>
          <div>
            <h2>Editing Task</h2>
            {editing }
            <EditTask
              setEditing={setEditing}
              curentTask={curentTask}
              updateTask={updateTask}
            />
          </div>
          <h2>Add user</h2>
          <AddTask addTask={addTask} />
        </div>
        <div className='flex-large'>
          <h2>View users</h2>
          <Task tasks={tasks} deleteTask={deleteTask} editTask={editTask} />
        </div>
      </div>
    </div>
  );
}

export default App;
